import {setErrorMsgAction} from "@src/redux/slices/errorSlice";
import {CategoryType} from "@root/interfaces/Categories";
import {Dispatch, SetStateAction} from "react";
import {IdNameType} from "@root/interfaces/Announcement";


type StateHelper = (
    state: any,
    setState: Dispatch<SetStateAction<any>>,
    dispatch: Dispatch<any>,
    fetchedData: any,
) => Promise<void>;

type SearchType = (IdNameType & { parent: any, icons: [], image: { url: string } })[];

const resolver = async (data) => {
    for (const key in data) {
        if (typeof data[key].then === 'function') {
            data[key] = await data[key];
        } else if (typeof data[key] === 'object' && !Array.isArray(data)) {
            data = resolver(data);
        }
    }
    return data;
};

export const setFetchedData: StateHelper = async (state, setState, dispatch, fetchedData) => {
    try {
        setState({...state, isFetch: true});
        fetchedData = resolver(fetchedData);
        setState({...state, ...fetchedData, isFetch: false});
    } catch (e) {
        dispatch(setErrorMsgAction(e.message));
        setState({...state, isFetch: false});
    }
};

export const categoryDataNormalization = (categoryList: CategoryType[]): CategoryType[] => (
    categoryList.map(category => {
        const childs = category.childs.map(child => ({...child, parent: category}));
        return {...category, childs};
    })
);

export const categorySearchHelper = (text: string, categoryList: CategoryType[]): SearchType => {
    const searchRegExp = RegExp(text, 'i');
    return categoryList
        .reduce((list, category) => {
            category.childs.forEach(sub_ctgr => {
                if (searchRegExp.test(sub_ctgr.name)) {
                    list.push(sub_ctgr);
                }
            })
            return list;
        }, []);
};
