import React, {FC} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {ItemsType} from "@root/interfaces/CustomMenuProps";
import {useStyles} from './useStyles';


export const AdvrtSettingsBlock: FC<any> = (props) => {
    const {
        isPreview,
        createAdvrt,
        adsParams,
        onBlur,
        handleMenuItem,
        handleListItem
    } = props;

    const data = createAdvrt.data;

    const classes = useStyles();
    return (
        <Grid
            container
            spacing={1}
            className={classes.root}
        >
            {
                Object.keys(data).map(key => {
                    return (
                        <>
                            {listGenerator(data[key], key)}
                            {
                                adsParams[key] && Object.keys(adsParams[key]).map(innerKey => {
                                    if (Array.isArray(adsParams[key][innerKey]) && adsParams[key][innerKey].length) {
                                        return listGenerator(adsParams[key][innerKey], innerKey)
                                    }
                                })
                            }
                        </>
                    );
                })
            }
        </Grid>
    )

    function listGenerator(data: ItemsType, key: string) {
        let field;

        if (key !== 'adsParams' && key !== 'address') {
            if (Array.isArray(data) && data.length) {
                if (key === 'body' || key === 'colors') {
                    field = data.map(item => (
                        <div
                            key={item.id}
                            onClick={handleListItem(key, item)}
                        >
                            {key === 'body'
                                ? (
                                    <>
                                        <img src={item.icon.url} alt={item.name}/>
                                        <Typography>{item.name}</Typography>
                                    </>
                                )
                                : (
                                    <div
                                        style={{
                                            width: '50px',
                                            height: '50px',
                                            backgroundColor: `${item.hex_color_code}`
                                        }}
                                    />
                                )}
                        </div>
                    ));
                } else {
                    data = [{id: null, name: 'Не выбрано'}, ...data];

                    field = (
                        <>
                            {isPreview
                                ? adsParams[key] && (
                                <Typography variant="subtitle1">
                                    <strong>{adsParams[key].name}</strong>
                                </Typography>
                            )
                                : (
                                    <CustomMenu
                                        valueName={adsParams[key] ? adsParams[key].name : data[0].name}
                                        items={data}
                                        onBlur={onBlur}
                                        onClick={handleMenuItem(key)}
                                    />
                                )}
                        </>
                    );
                }
                
                return (
                    <Grid
                        item
                        container
                        key={key}
                        xs={12}
                        sm={key === 'body' || key === 'colors' ? 12 : 6}
                    >
                        <Typography style={{width: '100%'}} variant="subtitle1">
                            <strong>{key}</strong>
                        </Typography>
                        {field}
                    </Grid>
                )
            }
        }
    }
};
