import React, {FC, useState} from 'react';
import {Grid, Hidden, Container} from '@material-ui/core';
import {useSelector} from "react-redux";
import {RootState} from "@src/redux/rootReducer";
import {MainLayout} from "@src/components/MainLayout";
import {AdvrtFormContainer} from './advrt_form/AdvrtFormContainer';
import {useStyles} from './useStyles';
import {i18n} from "@root/i18n";
import {
    fetchAdDataForCreate,
} from "@src/redux/slices/createAdvrtSlice";
import {CreateAdvrt} from "@src/components/advertisement/create_advrt/CreateAdvrt";


const initCreateAdState = {
    isFetch: false,
    error: null,
    adType: {
        id: null,
        name: 'Выберите тип объявления'
    },
    category: {
        id: null,
        name: 'Выберите категорию'
    },
    subCategory: {
        parentID: null,
        parentName: '',
        childs: []
    },
    data: {
        id: null,
        name: '',
        adsParams: [
            {
                currency: []
            }
        ]
    },
};

export const CreateAdvrtContainer: FC<void> = () => {
    const lang = i18n.language;

    const categoriesList = useSelector(({categories}: RootState) => categories.list);

    const [tabValue, setTabValue] = useState(0);

    const [createAdvrt, setCreateAdvrt] = useState(initCreateAdState);

    const [adTypes, setAdTypes] = useState(null);

    const handleTab = (_, newValue) => {
        setTabValue(newValue);
        switch (tabValue) {
            case 0:
                setCreateAdvrt({...createAdvrt, adType: adTypes[0]})
                break;
            case 1:
                setCreateAdvrt({...createAdvrt, adType: adTypes[1]})
                break;

        }
    };

    const handleCtgr = (parentID, parentName, list) => () => {
        if (list.length) {
            setCreateAdvrt({
                ...createAdvrt,
                subCategory: {
                    parentID,
                    parentName,
                    childs: list
                }
            });
        } else {
            setCreateAdvrt({
                ...createAdvrt,
                category: {
                    id: parentID,
                    name: parentName
                }
            });
        }
    };

    const handleSubCtgr = (ctgryID, subCtgryID) => () => {
        fetchAdDataForCreate({
            lang,
            ctgryID,
            subCtgryID
        });
        setCreateAdvrt({
            adTypes,
            subCategory: {
                id: subCtgry.parentID,
                name: subCtgry.parentName
            }
        });
    };


    const classes = useStyles();
    return (
        <MainLayout>
            <Container maxWidth="lg">
                <Grid container spacing={1}>
                    <Grid item xs={12} md={9}>
                        {
                            createAdvrt.category.id
                                ? <AdvrtFormContainer
                                    createAdvrt={createAdvrt}
                                />
                                : <CreateAdvrt/>
                        }
                    </Grid>
                    <Hidden smDown>
                        <Grid item md={3} className={classes.adBanner}>
                            <div className="right-banner"/>
                        </Grid>
                    </Hidden>
                </Grid>
            </Container>
        </MainLayout>
    )
}
