import * as yup from "yup";
import {sheets} from "jss";


export const requiredFields = {
    mileage: true,
    body: true,
    year: true,
    car_engine_type: true,
};

const requiredMsg = 'Обязательное поле!';

const schema = yup.lazy((value) => yup.object(
    Object.entries(value)
        .reduce(
            (a, [key]) => {
                if (requiredFields[key]) {
                    a[key] = yup.object({
                        id: yup.number().required(requiredMsg)
                    });
                    return a;
                }
            }, {}
        )
));

export const createAdvrtSchema = yup.object().shape({
