import React, {FC, Fragment, useEffect} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomFormikField} from "@src/components/elements/custom_formik_field/CustomFormikField";
import {isRequired} from '@root/validation_schemas/createAdvrtSchema';
import {AdvrtParams} from './AdvrtParams';
import {useStyles} from './useStyles';
import {dataPrepare} from "@src/components/advertisement/create_advrt/advrt_form/advrt_params_block/helpers";


export const AdvrtParamsBlock: FC<any> = (props) => {
    const {
        isPreview,
        createAdvrt,
        values,
        setValues,
        errors,
        touched,
        handleParamsInput,
    } = props;

    const initReqFields = {};

    const {subCategory} = createAdvrt;

    const {adParams} = values;
    const adParamsLen = Object.keys(values).length;

    const data = isPreview ? adParams : dataPrepare(subCategory.data);

    useEffect(() => {
        console.log(adParamsLen)
        setValues({...values, adParams: {...adParams, ...initReqFields}});
    }, [adParamsLen]);

    const classes = useStyles({isPreview});
    return (
        <Grid
            container
            spacing={1}
            className={classes.root}
        >
            {
                Object.keys(data).map(key => {
                    return (
                        <Fragment key={key}>
                            {
                                !isPreview && adParams[key]
                                    ? (
                                        <>
                                            {/*{listGenerator(data[key], key)}*/}
                                            {/*{*/}
                                            {/*    Object.keys(adParams[key]).map(innerKey => {*/}
                                            {/*        if (Array.isArray(adParams[key][innerKey]) && adParams[key][innerKey].length) {*/}
                                            {/*            return listGenerator(adParams[key][innerKey], innerKey)*/}
                                            {/*        }*/}
                                            {/*    })*/}
                                            {/*}*/}
                                            <AdvrtParams {...props} list={{data, key}}/>
                                        </>
                                    )
                                    : listGenerator(data[key], key)
                            }
                            {
                                key === 'body' && (
                                    <>
                                        <Grid
                                            item
                                            container
                                            xs={12}
                                            sm={6}
                                            className={classes.gridItem}
                                        >
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    year
                                                    {
                                                        isRequired(key)
                                                        && <span className='error-text'>*</span>
                                                    }
                                                </strong>
                                                {
                                                    errors.adParams
                                                    && errors.adParams.year
                                                    && touched.year
                                                    && <span className='error-text'> {errors.adParams.year}</span>
                                                }
                                            </Typography>
                                            {
                                                isPreview
                                                    ? <Typography>{adParams.year}</Typography>
                                                    : <CustomFormikField
                                                        className={
                                                            errors.adParams
                                                            && errors.adParams.year
                                                            && touched.year ? 'error-border' : ''
                                                        }
                                                        name='year'
                                                        placeholder='Год выпуска'
                                                        value={adParams.year ?? ''}
                                                        onChange={handleParamsInput('year')}
                                                    />
                                            }
                                        </Grid>
                                        <Grid
                                            item
                                            container
                                            xs={12}
                                            sm={6}
                                            className={classes.gridItem}
                                        >
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    mileage
                                                    {
                                                        isRequired(key)
                                                        && <span className='error-text'>*</span>
                                                    }
                                                </strong>
                                                {
                                                    errors.adParams
                                                    && errors.adParams.mileage
                                                    && touched.mileage
                                                    && <span className='error-text'> {errors.adParams.mileage}</span>
                                                }
                                            </Typography>
                                            {
                                                isPreview
                                                    ? <Typography>{adParams.mileage}</Typography>
                                                    : <CustomFormikField
                                                        className={
                                                            errors.adParams
                                                            && errors.adParams.mileage
                                                            && touched.mileage ? 'error-border' : ''
                                                        }
                                                        name='mileage'
                                                        placeholder='Пробег'
                                                        value={adParams.mileage ?? ''}
                                                        onChange={handleParamsInput('mileage')}
                                                    />
                                            }
                                        </Grid>
                                    </>
                                )
                            }
                        </Fragment>
                    )
                })
            }
        </Grid>
    )
}
