import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(() => ({
    root: {
        display: 'flex',
        justifyContent: 'center',
        width: '40px',
        height: '40px',
        backgroundColor: '#fafafa',
        borderRadius: '20px',
        zIndex: 2,
        '& button.left-arrow img': {
            transform: 'rotate(180deg)',
        },
    }
}));
