import React, {useState} from 'react'
import {IconButton, List, Typography} from '@material-ui/core'
import {CloseIcon} from '../../elements/icons'
import {ButtonComponent} from '../../elements/button/Button'
import {AuthRegForm} from '../../elements/auth_reg_form/AuthRegForm'
import {CustomSlider} from '../../elements/custom_slider/CustomSlider'
import {CustomListItem} from '../../elements/custom_list_item/CustomListItem'
import {settings} from './sliderSettings'
import {useStyles} from "./useStyles"


export const AuthRegSm = (props) => {
    const {t, language, handleCloseModal} = props;

    const [isAuthRegClicked, setIsAuthRegClicked] = useState(false);

    const authRegClickHandler = (value) => () => {
        setIsAuthRegClicked(value);
    };

    const onSubmit = (values, actions) => {
        actions.resetForm();
    };

    const classes = useStyles();
    return (
        <>
            {
                isAuthRegClicked
                    ? (
                        <AuthRegForm
                            t={t}
                            onSubmit={onSubmit}
                            language={language}
                            handleCloseModal={handleCloseModal}
                        />
                    )
                    : (
                        <div className={classes.authRegMenu}>
                            <div className='close-modal-block'>
                                <IconButton onClick={handleCloseModal}>
                                    <CloseIcon/>
                                </IconButton>
                            </div>
                            <div className='welcome-block'>
                                <Typography variant='h6'>
                                    {t('auth_reg:welcome')}
                                </Typography>
                            </div>
                            <div className='auth-reg-btn'>
                                <ButtonComponent onClick={authRegClickHandler(true)}>
                                    <Typography>
                                        {t('auth_reg:signInAndReg')}
                                    </Typography>
                                </ButtonComponent>
                            </div>
                            <div className='slider-block'>
                                <CustomSlider {...settings}>
                                    <img src={"img/bonus_img.png"} alt="bonus_img"/>
                                    <img src={"img/bonus_img.png"} alt="bonus_img"/>
                                    <img src={"img/bonus_img.png"} alt="bonus_img"/>
                                    <img src={"img/bonus_img.png"} alt="bonus_img"/>
                                </CustomSlider>
                            </div>
                            <div className='list-block'>
                                <List disablePadding={false}>
                                    <CustomListItem>
                                        <Typography variant='subtitle1'>Акции и бонусы</Typography>
                                    </CustomListItem>
                                    <CustomListItem>
                                        <Typography variant='subtitle1'>Помощь</Typography>
                                    </CustomListItem>
                                    <CustomListItem>
                                        <Typography variant='subtitle1'>Служба поддержки</Typography>
                                    </CustomListItem>
                                    <CustomListItem>
                                        <Typography variant='subtitle1'>Политика конфиденциальности</Typography>
                                    </CustomListItem>
                                </List>
                            </div>
                        </div>
                    )
            }
        </>
    )
};
