import React, {FC, Fragment} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomFormikField} from "@src/components/elements/custom_formik_field/CustomFormikField";
import {isRequired} from '@root/validation_schemas/createAdvrtSchema';
import {AdvrtParams} from './AdvrtParams';
import {dataPrepare} from "@src/components/advertisement/create_advrt/advrt_form/advrt_params/helpers";
import {useStyles} from './useStyles';


export const AdvrtParamsContainer: FC<any> = (props) => {
    const {
        isPreview,
        createAdvrt,
        values,
        errors,
        touched,
        handleParamsInput,
    } = props;


    const {subCategory} = createAdvrt;

    const {adParams} = values;

    const data = isPreview ? adParams : dataPrepare(subCategory.data, adParams);

    const classes = useStyles({isPreview});
    return (
        <Grid
            container
            spacing={1}
            className={classes.root}
        >
            {
                Object.keys(data).map(key => {
                    return (
                        <Fragment key={key}>
                            <AdvrtParams {...props} list={{data: data[key], key}}/>
                            {
                                key === 'body' && (
                                    <>
                                        <Grid
                                            item
                                            container
                                            xs={12}
                                            sm={6}
                                            className={classes.gridItem}
                                        >
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    year
                                                    {
                                                        isRequired(key)
                                                        && <span className='error-text'>*</span>
                                                    }
                                                </strong>
                                                {
                                                    errors.year
                                                    && touched.year
                                                    && <span className='error-text'> {errors.year}</span>
                                                }
                                            </Typography>
                                            {
                                                isPreview
                                                    ? <Typography>{adParams.year}</Typography>
                                                    : <CustomFormikField
                                                        className={
                                                            errors.year
                                                            && touched.year ? 'error-border' : ''
                                                        }
                                                        name='year'
                                                        placeholder='Год выпуска'
                                                        value={adParams.year ?? ''}
                                                        onChange={handleParamsInput('year')}
                                                    />
                                            }
                                        </Grid>
                                        <Grid
                                            item
                                            container
                                            xs={12}
                                            sm={6}
                                            className={classes.gridItem}
                                        >
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    mileage
                                                    {
                                                        isRequired(key)
                                                        && <span className='error-text'>*</span>
                                                    }
                                                </strong>
                                                {
                                                    errors.mileage
                                                    && touched.mileage
                                                    && <span className='error-text'> {errors.mileage}</span>
                                                }
                                            </Typography>
                                            {
                                                isPreview
                                                    ? <Typography>{adParams.mileage}</Typography>
                                                    : <CustomFormikField
                                                        className={
                                                            errors.mileage
                                                            && touched.mileage ? 'error-border' : ''
                                                        }
                                                        name='mileage'
                                                        placeholder='Пробег'
                                                        value={adParams.mileage ?? ''}
                                                        onChange={handleParamsInput('mileage')}
                                                    />
                                            }
                                        </Grid>
                                    </>
                                )
                            }
                        </Fragment>
                    )
                })
            }
        </Grid>
    )
}
