import React, {FC} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {ItemsType} from "@root/interfaces/CustomMenuProps";
import {useStyles} from './useStyles';


export const AdvrtSettingsBlock: FC<any> = (props) => {
    const {
        isPreview,
        createAdvrt,
        adsParams,
        onBlur,
        handleMenuItem,
        handleListItem
    } = props;

    const data = isPreview ? adsParams : createAdvrt.data;

    const classes = useStyles();
    return (
        <Grid
            container
            spacing={1}
            className={classes.root}
        >
            {
                Object.keys(data).map(key => {
                    return (
                        <>
                            {listGenerator(data[key], key)}
                            {
                                !isPreview && adsParams[key] && Object.keys(adsParams[key]).map(innerKey => {
                                    if (Array.isArray(adsParams[key][innerKey]) && adsParams[key][innerKey].length) {
                                        return listGenerator(adsParams[key][innerKey], innerKey)
                                    }
                                })
                            }
                        </>
                    );
                })
            }
        </Grid>
    )

    function listGenerator(data: ItemsType, key: string) {
        let fields;
        console.log(data)
        if (
            key !== 'adsParams'
            && key !== 'address'
            && key !== 'id'
            && key !== 'name'
        ) {
            if ((key === 'body' || key === 'colors') && Array.isArray(data) && data.length) {
                fields = data.map(item => (
                    <div
                        key={item.id}
                        onClick={handleListItem(key, item)}
                    >
                        {
                            key === 'body'
                                ? (
                                    <>
                                        <img src={item.icon.url} alt={item.name}/>
                                        <Typography>{item.name}</Typography>
                                    </>
                                )
                                : (
                                    <div
                                        style={{
                                            width: '50px',
                                            height: '50px',
                                            backgroundColor: `${item.hex_color_code}`
                                        }}
                                    />
                                )
                        }
                    </div>
                ));
            } else {
                if (Array.isArray(data)) data = [{id: null, name: 'Не выбрано'}, ...data];

                fields = isPreview
                    ? key === 'body' || key === 'colors'
                        ? key === 'body' ? <img src={data.icon.url} alt={data.name}/> : <div
                            style={{
                                width: '50px',
                                height: '50px',
                                backgroundColor: `${data.hex_color_code}`
                            }}
                        />
                        : <Typography>{data.name}</Typography>
                    : <CustomMenu
                        valueName={adsParams[key] ? adsParams[key].name : data[0].name}
                        items={data}
                        onBlur={onBlur}
                        onClick={handleMenuItem(key)}
                    />;
            }
            return (
                <Grid
                    item
                    container
                    key={key}
                    xs={12}
                    direction={isPreview ? 'row' : null}
                    sm={(key === 'body' || key === 'colors') && !isPreview ? 12 : 6}
                >
                    <Typography style={{width: '100%'}} variant="subtitle1">
                        <strong>{key}</strong>
                    </Typography>
                    {fields}
                </Grid>
            )
        }
    }
};
