import React from "react"
// import {useDispatch} from 'react-redux'
import {setLocal} from "../../../redux/actions/localizationActions"
import {Typography} from "@material-ui/core"
import {useStyles} from './useStyles'
import {GetStaticProps} from "next";
import {wrapper} from "../../../redux/store";


export const Localization = (props) => {
    // const dispatch = useDispatch();
    console.log(props)
    const setLocalAction = (lang) => () => {
        props.store.dispatch(setLocal(lang))
    };
    const classes = useStyles()

    return (
        <div className={classes.root}>
            <Typography
                variant="subtitle1"
                className="localization-item"
                onClick={setLocalAction('ru')}
            >
                Ру
            </Typography>
            <Typography
                variant="subtitle1"
                className="localization-item"
                onClick={setLocalAction('uz')}
            >
                O’z
            </Typography>
        </div>
    )
};

export const getStaticProps: GetStaticProps = wrapper.getStaticProps(({store}) => {
    return {
        store
    }
});
