import {mainSliderActionsTypes} from '../actions/mainSliderActions';

const initialState = {
    images: [],
    errors: [],
    isFetching: false,
};

function mainSlider(state = initialState, action) {
    switch (action.type) {
        case mainSliderActionsTypes.FETCH_REQUEST: {
            return {
                ...state,
                isFetching: true,
            };
        }
        case mainSliderActionsTypes.GET_SUCCESS: {
            return {
                ...state,
                images: action.images,
                isFetching: false,
            };
        }
        case mainSliderActionsTypes.GET_ERROR: {
            return {
                ...state,
                images: [],
                errors: action.errors,
                isFetching: false,
            };
        }
        default:
            return state;
    }
}

export default mainSlider;
