export const settings = {
    dots: false,
    infinite: true,
    // autoplay: true,
    arrows: false,
    centerMode: true,
    // centerPadding: "0px",
    slidesToShow: 3,
    slidesToScroll: 1,
    speed: 500,
    autoplaySpeed: 5000,
    variableWidth: true,
    responsive: [
        {
            breakpoint: 1200,
            settings: {
                centerMode: true,
                slidesToShow: 3,
            }
        },
        {
            breakpoint: 1024,
            settings: {
                centerMode: true,
                slidesToShow: 2,
            }
        },
        {
            breakpoint: 768,
            settings: {
                centerMode: true,
                slidesToShow: 1,
            }
        }
    ]
}
