import React, {Dispatch, PropsWithChildren, ReactChildren} from "react"
import Drawer from '@material-ui/core/Drawer';
import {useStyles} from './useStyles';

type DrawerProps = {
    children: any,
    isOpen: PropsWithChildren<boolean>,
    setIsOpen: Dispatch<boolean>
};

export const LeftDrawer = ({children, isOpen, setIsOpen}: DrawerProps) => {
    const toggleDrawer = (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }
        setIsOpen(false);
    };

    const classes = useStyles();

    return (
        <Drawer className={classes.root} anchor='left' open={isOpen} onClose={toggleDrawer}>
            {children}
        </Drawer>
    )
};
