import {userApi} from '../api/api'
import {call, put, takeLatest} from "redux-saga/effects";
import {selfDataActionTypes, selfDataActions} from "./actions/selfDataActions";

const {fetchSelfDataBegin, fetchSelfDataSuccess, fetchSelfDataFailure} = selfDataActions;

function* getApiData(phone, password) {
    try {
        yield put(fetchSelfDataBegin());
        const data = yield call(userApi.login, phone, password);
        yield put(fetchSelfDataSuccess(data));
    } catch (e) {
        yield put(fetchSelfDataFailure(e));
    }
}

export default function* rootSaga() {
    yield takeLatest(selfDataActionTypes.FETCH_SELF_DATA_BEGIN, getApiData);
}
