import React from "react";
import {useScrollTrigger} from "@material-ui/core";


export const withScrollThreshold = (Component, threshold) => props => {
    const isThreshold = useScrollTrigger({disableHysteresis: true, threshold});
    return (
        <div>
            <Component {...props} isThreshold={isThreshold}/>
        </div>
    );
};
