import {makeStyles} from '@material-ui/core/styles';

export const useStyles = makeStyles(() => ({
    root: {
        '& h2.title': {
            fontWeight: '400'
        },
        '& div.slider': {
            marginTop: '15px',
            '& div.slick-slide': {
                padding: '14px 7px',
                margin: '-14px -6px 0',
            },
            '& button.slick-prev': {
                left: '-10px'
            },
            '& button.slick-next': {
                right: '-10px'
            },
            '& button.slick-disabled': {
                display: 'none'
            }
        }
    }
}));
