import makeStyles from '@material-ui/core/styles/makeStyles'


export const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > h6': {
            lineHeight: 1,
            cursor: 'pointer',
            '&:first-child': {
                paddingRight: '4px',
                borderRight: '1px #000 solid'
            },
            '&:last-child': {
                paddingLeft: '4px',
            }
        }
    },
    selected:{
        color: theme.palette.primary.main
    }
}))
