import React, { useState } from 'react'
import { Carousel } from 'react-responsive-carousel'
import Image from 'common/img/slider-img.jpg'
import { Container } from '@material-ui/core'

import { useStyles } from './useStyle'

export const TopSlider = () => {
    const classes = useStyles()

    return (
        <Container maxWidth="lg">
            <Carousel
                className={classes.root}
                showIndicators={false}
                // centerSlidePercentage={80}
                showArrows
                showStatus={false}
                showIndicators={false}
                infiniteLoop={false}
                showThumbs={false}
                selectedItem={0}
                // useKeyboardArrows
                // autoPlay
                interval={5000}
                stopOnHover
                swipeable
                dynamicHeight
                // emulateTouch
            >
                <div>
                    <img src={Image} />
                </div>
                <div>
                    <img src={Image} />
                </div>
                <div>
                    <img src={Image} />
                </div>
                <div>
                    <img src={Image} />
                </div>
                <div>
                    <img src={Image} />
                </div>
                <div>
                    <img src={Image} />
                </div>
            </Carousel>
        </Container>
    )
}
