import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(() => ({
    root: {
        marginTop: '20px',
        position: 'relative',
        '& div.slick-slider': {
            zIndex: 1,
            '& img': {
                width: '97%',
                margin: 'auto'
            }
        },
        '& button span svg:hover': {
            fill: '#a153ff',
        },
        '& div.slider-arrows-container': {
            position: 'absolute',
            top: 0,
            display: 'flex',
            alignItems: 'center',
            width: '100%',
            height: '100%',
            '& > div.MuiContainer-root': {
                display: 'flex',
                justifyContent: 'space-between',
                '& > div': {
                    width: '40px',
                    height: '40px',
                    backgroundColor: '#fafafa',
                    zIndex: 2,
                },
                // '& div.left-arrow:after, & div.right-arrow:after': {
                //     width: '10px',
                //     height: '10px',
                // },
                '& div.left-arrow::after': {
                    content: '<',
                    marginLeft: '8px',
                    width: '10px',
                    height: '10px',
                },
                '& div.right-arrow::after': {
                    content: '>',
                    marginRight: '8px',
                }
            }
        }
    }
}));
