import React, {FC, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Tabs, Typography, InputBase, List, ListItem, Grid} from "@material-ui/core";
import {RootState} from "@src/redux/rootReducer";
import {CustomTab} from "@src/components/elements/custom_tab/CustomTab";
import {fetchAdDataForCreate} from "@src/redux/slices/createAdvrtSlice";
import {i18n} from "@root/i18n";
import {useStyles} from './useStyles';


export const CreateAdModalForm: FC<any> = (props) => {
    const {handleCloseModal} = props;

    const lang = i18n.language;

    const dispatch = useDispatch();
    const categoriesList = useSelector(({categories}: RootState) => categories.list);

    const [subCtgry, setSubCtgry] = useState({ctgrID: null, childs: []});
    const [tabValue, setTabValue] = useState(0);

    const handleTab = (_, newValue) => {
        setTabValue(newValue);
    };

    const handleCtgr = (ctgrID, list) => () => {
        setSubCtgry({ctgrID, childs: list});
    };

    const handleSubCtgr = (ctgrID, subCtgrID) => () => {
        dispatch(fetchAdDataForCreate({
            lang,
            ctgryID: ctgrID,
            subCtgryID: subCtgrID,
        }))
    };


    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Tabs
                value={tabValue}
                onChange={handleTab}
                centered
            >
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Объявления
                        </Typography>
                    }
                    id={0}
                />
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Торги
                        </Typography>
                    }
                    id={1}
                />
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Продвинутые торги
                        </Typography>
                    }
                    id={2}
                />
            </Tabs>
            <Grid container className={classes.tabsContent}>
                <Grid item xs={3} className='categories-block'>
                    <div className='header'>
                        <Typography>Выберите категорию</Typography>
                    </div>
                    <List className='categories-list'>
                        {
                            categoriesList.map((ctgr) => (
                                <ListItem key={ctgr.id} onClick={handleCtgr(ctgr.id, ctgr.childs)}>
                                    <div>
                                        <img src={ctgr.icons.url} alt='category'/>
                                        {ctgr.name}
                                    </div>
                                </ListItem>
                            ))
                        }
                    </List>
                </Grid>
                <Grid item xs={9} className='sub-categories-block'>
                    <div className='search-block'>
                        <InputBase style={{border: '1px solid'}} placeholder='Поиск по категориям'/>
                    </div>
                    <div>
                        <List className='categories-list'>
                            {
                                subCtgry.childs.map((ctgr) => (
                                    <ListItem key={ctgr.id} onClick={handleSubCtgr(subCtgry.ctgrID, ctgr.subCtgrID)}>
                                        <div>
                                            {ctgr.name}
                                        </div>
                                    </ListItem>
                                ))
                            }
                        </List>
                    </div>
                </Grid>
            </Grid>
        </div>
    )
};
