import {userApi} from '../../api/api'
import {authErrorAction} from './errorsActions'

export const selfDataActionTypes = {
    GET_SELF_DATA: 'GET_SELF_DATA'
};

export const login = async (phone, password) => {
    try {
        const selfData = await userApi.login(phone, password);
        return {
            type: selfDataActionTypes.GET_SELF_DATA,
            payload: {...selfData.userInfo}
        }
    } catch (e) {
        authErrorAction(e)
    }
};
