import {createAction, createSlice} from '@reduxjs/toolkit'

export const setIsAuthAction = createAction('auth/setAuth', (isAuth) => ({payload: isAuth}));
export const fetchTokenBeginAction = createAction('token/beginFetch');
export const fetchTokenSuccessAction = createAction('token/fetchSuccess');
export const fetchTokenFailureAction = createAction('token/fetchFailure');

export type AuthTypes = {
    isFetch: boolean,
    isAuth: boolean,
    error?: any
};

const initialState: AuthTypes = {
    isFetch: false,
    isAuth: false,
    error: null
};

export const authReducer = createSlice({
    name: 'auth',
    initialState,
    reducers:{
        [setIsAuthAction.type]: (state, action) => state.isAuth = action.payload,
        [fetchTokenBeginAction.type]: (state) => ({
            ...state,
            isFetch: true,
            error: null
        }),
        [fetchTokenSuccessAction.type]: (state, action) => ({
            ...state,
            isFetch: false,
            ...action.payload
        }),
        [fetchTokenFailureAction.type]: (state, action) => ({
            ...state,
            isFetch: false,
            error: action.payload
        })
    }
});
