import Axios from 'axios';

const instance = Axios.create({
    withCredentials: true,
    baseURL: 'http://localhost:4000/api/'
});


export const userApi = {
    getMainSliderImgs() {
        return instance.get(`getMainSliderImgs`)
            .then((res) => {
                return res.data;
            })
            .catch(err => {
                throw (err.response);
            });
    }
}
