import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
import {Categories, CategoryType} from '@root/interfaces/Categories'
import {userAPI} from "@src/api/api";
import {setErrorMsgAction} from './errorSlice';


const initCategory = {
    id: null,
    name: '',
    images: {
        id: null,
        url: {
            default: ''
        }
    },
    icons: {
        id: null,
        url: {
            default: ''
        }
    },
    childs: [],
    has_auction: null
};

const initCategories = Array.from({length: 11}).map(() => initCategory);

const initialState: Categories = {
    isFetch: false,
    error: null,
    list: initCategories,
};

// Async thunk
export const fetchCategories = createAsyncThunk<CategoryType[], string>(
    'categories/fetchCategories',
    async (lang) => {
        try {
            return await userAPI.getCategories('lang');
        } catch (e) {
            setErrorMsgAction(e.message);
        }
    }
);

const advertisementSlice = createSlice({
    name: 'categories',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(fetchCategories.pending, (state) => {
            state.isFetch = true;
            state.error = null;
        });
        builder.addCase(fetchCategories.fulfilled, (state, action) => {
            state.isFetch = false;
            state.error = null;
            state.list = action.payload;
        });
        builder.addCase(fetchCategories.rejected, (state, action) => {
            state.isFetch = false;
            state.error = action.payload;
        });
    }
});

export const categoriesReducer = advertisementSlice.reducer;
