import * as yup from "yup";


export const requiredFields = {
    year: true,
    mileage: true,
    body: true,
    manufacturer: true,
    model: true,
    colors: true,
    car_engine_type: true,
    transmission: true,
};

const requiredMsg = 'Обязательный параметр!';

const lazy = yup.lazy((value) => yup.object(
    Object.entries(value)
        .reduce(
            (a, [key]) => {
                if (requiredFields[key]) {
                    a[key] = yup.object({
                        id: yup.number().nullable().required(requiredMsg)
                    });
                    return a;
                }
            }, {}
        )
));

export const createAdvrtSchema = yup.object().shape({
    adType: yup.object({
        id: yup.number().nullable().required(requiredMsg)
    }),
    category: yup.object({
        id: yup.number().nullable().required(requiredMsg)
    }),
    title: yup.string().required(requiredMsg),
    price: yup.string().required(requiredMsg),
    currency: yup.string().required(requiredMsg),
    location: yup.object({
        city_id: yup.number().required(requiredMsg),
    }).nullable().required(requiredMsg),
    files: yup.array().required(requiredMsg),
    description: yup.string().required(requiredMsg),
    phone: yup.string().required(requiredMsg),
    adParams: lazy
});
