import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        position: 'fixed',

        '& div.burger-menu': {
            [theme.breakpoints.down('md')]: {
                display: 'flex',
            },
            [theme.breakpoints.up('md')]: {
                display: 'none',
            },
        },

        '& div.location': {
            display: 'flex',
            fontSize: '14px',
            [theme.breakpoints.down('sm')]: {
                display: 'none',
            }
        },

        '& img.social-icons': {
            marginRight: '10px',
            width: '35px',

            [theme.breakpoints.down('1000')]: {
                display: 'none',
            },
            '&:hover': {
                cursor: 'pointer',
            },
        },
        '& div.multiple': {
            [theme.breakpoints.down('1000')]: {
                display: 'none',
            },
        },
        '&  div.help-content': {
            display: 'flex',
            flexFlow: 'row wrap',
            '& a': {
                display: 'flex',
                textDecoration: 'none',
                alignItems: 'center',
                color: '#000',
            },
        },
        '& div > a img.help-content-icon': {
            textDecoration: 'none',
            height: '15px',
            marginRight: '10px',
        },
        '& img.pl-icon': {
            width: '32px',
            height: '23px',
        },
        '& div.display-md': {
            [theme.breakpoints.down('md')]: {
                display: 'none',
            },
        },
        '&   div.localization ': {
            display: 'flex',
        },
        '&  h6.localization-item:first-child': {
            marginRight: '8px',
        },
        '& h6.select-region': {
            textDecoration: 'underline',
        },
        //    Header bottom content

        '& div.MuiFormControl-root': {
            width: '100%',
            borderRadius: '7px',
        },
        '& button.category-button': {
            color: '#fff',
            width: '80%',
            [theme.breakpoints.down('1000')]: {
                display: 'none',
            },
        },
        '& span.MuiButton-label': {
            textTransform: 'none',
        },
        '& form.MuiPaper-root': {
            width: '100%',
        },
        '& img.search-icon': {
            height: '20px',
            margin: '8px -35px 0 0',
            zIndex: 1
        },
        '& div.adv-type, button.add-adv': {
            [theme.breakpoints.down('1000')]: {
                display: 'none',
            },
        },

        '& div.search-block > div form': {
            display: 'flex',
            width: '100%',
        },
        '& input.search-input': {
            padding: '10px 62px 10px 45px',
            width: '100%',
            borderBottomRightRadius: '7px',
            borderTopRightRadius: '7px',
            borderBottomLeftRadius: '7px',
            borderTopLeftRadius: '7px',
            border: '1px solid #ccc',
            fontSize: '0.87rem',
        },
        '& button.search-button': {
            marginLeft: '-64px',
            borderBottomRightRadius: '7px',
            borderTopRightRadius: '7px',
            borderBottomLeftRadius: '0',
            borderTopLeftRadius: '0',
            borderStyle: 'none',
            backgroundColor: '#97aaa5',
            lineHeight: '1.65',
            '& h6': {
                [theme.breakpoints.down('md')]: {
                    fontSize: '0.8rem',
                },
                [theme.breakpoints.down('lg')]: {
                    fontSize: '0.87rem',
                },
            },
        },
        '& div.MuiSelect-selectMenu': {
            padding: '5px 8px',
            '& > p': {
                textAlign: 'center'
            }
        },
        '& button.MuiButton-root': {
            padding: '5px 0px',
        },

        //    Adaptive
        '& div.header-top-content': {
            [theme.breakpoints.down('1000')]: {
                justifyContent: 'space-between',
            },
        },

        '& h6': {
            [theme.breakpoints.between('md', 'lg')]: {
                fontSize: '0.87rem',
            },
            [theme.breakpoints.down('md')]: {
                fontSize: '0.87rem',
            },
            [theme.breakpoints.down('lg')]: {
                fontSize: '0.8rem',
            },
            [theme.breakpoints.down('1140')]: {
                fontSize: '0.8rem',
            },
            [theme.breakpoints.down('1100')]: {
                fontSize: '0.8rem',
            },
            [theme.breakpoints.up('lg')]: {
                fontSize: '0.87rem',
            },
        },

        '& button.bottom-sign-button': {
            [theme.breakpoints.up('1000')]: {
                display: 'flex',
            },
            [theme.breakpoints.down('1000')]: {
                display: 'none',
            },
        },

        '& button.top-sign-button': {
            [theme.breakpoints.up('1000')]: {
                display: 'none',
            },
            [theme.breakpoints.down('1000')]: {
                display: 'flex',
            },
        },
    },
}))
