import React, {FC, useEffect, useState} from 'react';
import {Grid, Hidden, Container, Typography} from '@material-ui/core';
import {useSelector} from "react-redux";
import {i18n} from "@root/i18n";
import {userAPI} from '@src/api/api';
import {RootState} from "@src/redux/rootReducer";
import {MainLayout} from "@src/components/MainLayout";
import {AdvrtFormContainer} from './advrt_form/AdvrtFormContainer';
import {CreateAdvrt} from "@src/components/advertisement/create_advrt/CreateAdvrt";
import {useStyles} from './useStyles';


const initCreateAdState = {
    isFetch: false,
    error: null,
    adType: {
        id: null,
        name: '',
        currency: [],
        expired: [],
    },
    category: {
        id: null,
        name: '',
        childs: []
    },
    subCategory: {
        id: null,
        name: '',
        data: {
            adsParams: []
        }
    }
};

export const CreateAdvrtContainer: FC<void> = () => {
    const lang = i18n.language;

    const categoriesList = useSelector(({categories}: RootState) => categories.list);

    const [tabValue, setTabValue] = useState(1);

    const [adTypes, setAdTypes] = useState([]);

    const [createAdvrt, setCreateAdvrt] = useState(initCreateAdState);
    const {category} = createAdvrt;

    const [isForm, setIsForm] = useState(false);

    const getAdTypes = async () => {
        try {
            setCreateAdvrt({...createAdvrt, isFetch: true});

            const adTypes = await userAPI.getAdTypes(lang);

            setCreateAdvrt({...createAdvrt, isFetch: false});

            setAdTypes(adTypes);
            setCreateAdvrt({
                ...createAdvrt,
                adType: adTypes[0]
            })
        } catch (error) {
            setCreateAdvrt({...createAdvrt, error});
        }
    };

    const handleTab = (_, newValue) => {
        setTabValue(newValue);
    };

    const setAdType = () => {
        const [selectedAdType] = adTypes.filter(type => type.id === tabValue);

        setCreateAdvrt({
            ...createAdvrt,
            adType: selectedAdType
        });
    };

    const handleCategory = (category) => () => {
        setCreateAdvrt({
            ...createAdvrt,
            error: null,
            category
        });
    };

    const handleSubCategory = (id, name) => async () => {
        try {
            if (id !== undefined) {
                setCreateAdvrt({
                    ...createAdvrt,
                    isFetch: true
                });

                const data: any = await userAPI.getAdDataForCreate(category.id, id, lang);

                setIsForm(true);

                setCreateAdvrt({
                    ...createAdvrt,
                    isFetch: false,
                    subCategory: {id, name, data}
                });
            } else {
                setIsForm(true);
                setCreateAdvrt({
                    ...initCreateAdState,
                    adType: createAdvrt.adType,
                    category: createAdvrt.category
                });
            }
        } catch ({message}) {
            setCreateAdvrt({
                ...createAdvrt,
                error: message
            });
        }
    };

    useEffect(() => {
        getAdTypes();
    }, []);

    useEffect(() => {
        adTypes.length && setAdType();
    }, [tabValue]);

    const classes = useStyles();
    return (
        <MainLayout>
            <Container maxWidth="lg">
                <Grid container spacing={2} className={classes.root}>
                    <Grid item xs={12} md={9}>
                        {createAdvrt.isFetch
                            ? <Typography>...Loading</Typography>
                            : (
                                isForm
                                    ? <AdvrtFormContainer
                                        setIsForm={setIsForm}
                                        createAdvrt={createAdvrt}
                                    />
                                    : <CreateAdvrt
                                        handleTab={handleTab}
                                        handleCategory={handleCategory}
                                        handleSubCategory={handleSubCategory}
                                        adTypes={adTypes}
                                        tabValue={tabValue}
                                        createAdvrt={createAdvrt}
                                        categoriesList={categoriesList}
                                    />
                            )}
                    </Grid>
                    <Hidden smDown>
                        <Grid item md={3} className='advrt-banner'>
                            <div/>
                        </Grid>
                    </Hidden>
                </Grid>
            </Container>
        </MainLayout>
    )
}
