import makeStyles from '@material-ui/core/styles/makeStyles'

export const useStyles = makeStyles(() => ({
    root: {
        display: 'flex',
        '& h6': {
            lineHeight: 1
        },
        '& > h6.localization-item:first-child': {
            paddingRight: '4px',
            borderRight: '1px solid'
        },
        '& > h6.localization-item:last-child': {
            paddingLeft: '4px',
        }
    }
}))
