import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(() => ({
    root: {
        width: '100%',
        height: '100%',
        background: 'url(img/modal_auth_sm_bg.svg) no-repeat cover',
        // backgroundRepeat: 'no-repeat'
    }
}))
