import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(({palette}) => ({
    root: {
        padding: '10px',
        background: 'linear-gradient(311.22deg, #675EAA 14.56%, #8D84CF 85.44%)',
        '& svg': {
            width: '12px',
            height: '12px'
        },
        // '&:hover svg > path': {
        //     fill: palette.primary.error
        // }
    }
}));
