import {AnyAction} from "redux"
import {HYDRATE} from "next-redux-wrapper"
import {ISelfData} from "../../../interfaces/ISelfData"
import {selfDataActionTypes} from '../actions/selfDataActions'

const initialState = {
    isFetch: false,
    userData: {
        id: null,
        name: '',
        email: '',
        phone: '',
        email_verified_at: '',
        created_at: '',
        updated_at: ''
    },
    error: null
};

export const selfData = (state: ISelfData = initialState, action: AnyAction): ISelfData => {
    switch (action.type) {
        case HYDRATE:
            return {
                ...state,
                ...action.payload.selfData
            }
        case selfDataActionTypes.FETCH_SELF_DATA_BEGIN:
            return {
                ...state,
                isFetch: true,
                error: null
            }
        case selfDataActionTypes.FETCH_SELF_DATA_SUCCESS:
            return {
                ...state,
                isFetch: false,
                ...action.payload
            }
        case selfDataActionTypes.FETCH_SELF_DATA_FAILURE:
            return {
                ...state,
                isFetch: false,
                error: action.payload
            }
        default:
            return state;
    }
};
