import * as yup from "yup";


const requiredList = {
    drive: true,
    seats: true
};

const lazyFunc = (value) => {
    const valueKeys = Object.keys(value);
    const requiredFields = yup;

    valueKeys.forEach(key => {
        if (requiredList[key]) {
            requiredFields.object({
                [key]: yup.object({
                    id: yup.number().required('Обязательный параметр!')
                })
            });
        }
    });
    console.log(requiredFields)
    return requiredFields;
};

export const createAdvrtSchema = yup.object({
    // title: yup.string().required('Обязательное поле!'),
    // location: yup.string().required('Обязательное поле!'),
    // files: yup.array().required('Загрузите фотографии!'),
    // description: yup.string().required('Обязательное поле!'),
    // phone: yup.string().required('Обязательное поле!'),
    adsParams: yup.lazy(lazyFunc)
});
