import {isRequired} from "@root/validation_schemas/createAdvrtSchema";
import {Grid, Typography} from "@material-ui/core";
import {CustomCheckbox} from "@src/components/elements/custom_checkbox/CustomCheckbox";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import React, {FC} from "react";
import {useStyles} from "./useStyles";


export const AdvrtParams: FC<any> = (props) => {
    const {
        list,
        errors,
        touched,
        isPreview,
        onBlur,
        values,
        handleMenuItem,
        handleListItem,
        handleParamsCheckbox,
    } = props;

    let {key, data} = list;
    const {adParams} = values;

    if (key === 'adsParams' && data.length) {
        key = 'condition';
        data = data[0][key];
    }

    // const isExcludedRows = key !== 'year' && key !== 'mileage';

    const isOptions = key === 'safety'
        || key === 'multimedia'
        || key === 'assistant'
        || key === 'exterior';

    const isSpecialRows = key === 'body' || key === 'colors' || isOptions;

    let fields = null;

    const classes = useStyles({isPreview});
    if (data) {
        if (isSpecialRows && data.length) {
            fields = (
                <>
                    <Typography variant="subtitle1">
                        {key}
                        {
                            isRequired(key)
                            && <span className='error-text'>*</span>
                        }
                        {
                            errors.adParams
                            && errors.adParams[key]
                            && touched.adParams
                            && touched.adParams[key]
                            && touched.adParams[key].id
                            && <span className='error-text'> {errors.adParams[key].id}</span>
                        }
                    </Typography>
                    <div className='row-list'>
                        {
                            data.map(item => (
                                <div key={item.id}>
                                    {
                                        key === 'body'
                                            ? <div
                                                onClick={!isPreview ? handleListItem(key, item) : null}
                                                className={
                                                    adParams[key]
                                                    && adParams[key].id === item.id
                                                        ? classes.selected : ''
                                                }
                                            >
                                                {/*<img src={item.icon.url} alt={item.name}/>*/}
                                                <Typography>{item.name}</Typography>
                                            </div>
                                            : <>
                                                {key === 'colors'
                                                    ? <div
                                                        onClick={!isPreview ? handleListItem(key, item) : null}
                                                        className={adParams[key] && adParams[key].id === item.id ? classes.selected : ''}
                                                        style={{
                                                            width: '50px',
                                                            height: '50px',
                                                            backgroundColor: `${item.hex_color_code}`,
                                                        }}
                                                    />
                                                    : <div style={{display: 'flex', alignItems: 'center'}}>
                                                        <CustomCheckbox
                                                            disabled={isPreview}
                                                            checked={
                                                                adParams[key] && adParams[key].some(val => val.id === item.id)
                                                            }
                                                            onChange={handleParamsCheckbox(key, item)}
                                                        />
                                                        <Typography>{item.name}</Typography>
                                                    </div>}
                                            </>
                                    }
                                </div>
                            ))
                        }
                    </div>
                </>
            );
        } else {
            if (!Array.isArray(data)) {
                fields = (
                    <>
                        <Typography variant="subtitle1">
                            {key}
                        </Typography>
                        {
                            key === 'body'
                                ? <Typography>{data.name}</Typography>
                                : key === 'colors'
                                ? <div
                                    style={{
                                        width: '50px',
                                        height: '50px',
                                        backgroundColor: `${data.hex_color_code}`
                                    }}
                                />
                                : <Typography>{data.name}</Typography>
                        }
                    </>
                );
            } else if (data.length) {
                if (key !== 'condition') data = [{id: null, name: 'Не выбрано'}, ...data];
                fields = (
                    <>
                        <Typography variant="subtitle1">
                            <strong>
                                {key}
                                {
                                    isRequired(key)
                                    && <span className='error-text'>*</span>
                                }
                            </strong>
                            {
                                errors.adParams
                                && touched.adParams
                                && errors.adParams[key]
                                && touched.adParams[key]
                                && touched.adParams[key].id
                                && <span className='error-text'> {errors.adParams[key].id}</span>
                            }
                        </Typography>
                        <CustomMenu
                            className={
                                errors.adParams
                                && touched.adParams
                                && errors.adParams[key]
                                && touched.adParams[key] ? 'error-border' : ''
                            }
                            name={key}
                            onBlur={onBlur}
                            items={data}
                            valueName={adParams[key] ? adParams[key].name : data[0].name}
                            onClick={handleMenuItem(key)}
                        />
                    </>
                );
            }
        }
        fields = fields
            ? <Grid
                item
                container
                className={classes.gridItem}
                key={key}
                xs={12}
                sm={isSpecialRows && !isPreview || isOptions ? 12 : 6}
            >
                {fields}
            </Grid>
            : <></>
    }

    return fields;
}
