import * as yup from "yup";


export const requiredFields = {
    mileage: true,
    body: true,
    year: true,
    car_engine_type: true,
};

const requiredMsg = 'Обязательное поле!';

const lazy = yup.object((value) => {
    const keys = Object.keys(requiredFields);
    const adParams = yup.object();

    keys.forEach(k => {
        if (value[k] !== undefined) {
            adParams.fields[k] = yup.object({
                id: yup.number().required(requiredMsg)
            })
        }
    });

    console.log(adParams)
    if (Object.keys(adParams.fields).length === 0) {
        return yup.mixed().notRequired();
    }

    return adParams;
});

