import {TOTAL_FILES_LIMIT, TOTAL_FILES_SIZE_LIMIT} from '@root/src/constants';
import React, {Dispatch, FC} from 'react';
import {DragDropContext, Droppable, Draggable, resetServerContext} from 'react-beautiful-dnd';
import {useStyles} from './useStyles';


export const PreviewPhotos: FC<{ images: [], setImages: Dispatch<any> }> = (props) => {
    const {images, setImages} = props;

    const handleOnDragEnd = (result) => {
        if (!result.destination) return;

        const items = Array.from(images);
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);

        setImages(items);
    };

    const handleUploadFile = ({target}) => {
        if (target.files[0] && images.length < TOTAL_FILES_LIMIT) {

            const totalSize = images.reduce((total, img: { file: { size: number } }) => total + img.file.size, 0);

            if ((totalSize + target.files[0].size) < TOTAL_FILES_SIZE_LIMIT) {
                setImages([{
                    file: target.files[0],
                    img: URL.createObjectURL(target.files[0])
                },
                    ...images
                ]);
            }
        }
    };

    const removeFile = (id) => {
        images.map((file, index) => {
            if (file.img === id) {
                setImages(images.splice(index, 1))
            }
        })
    };
    console.log(images)
    resetServerContext();

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <input type='file' onChange={handleUploadFile} accept="image/png,image/jpeg"/>
            <DragDropContext onDragEnd={handleOnDragEnd}>
                <Droppable droppableId="images" direction="horizontal">
                    {(provided) => (
                        <div className="images-wrapper" {...provided.droppableProps} ref={provided.innerRef}>
                            {images.map(({file, img}: any, index) => {
                                return (
                                    <Draggable key={img} draggableId={img} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                                <img
                                                    src={img}
                                                    style={{width: '160px', height: '120px', objectFit: 'cover'}}
                                                    alt={file.name}
                                                />
                                            </div>
                                        )}
                                    </Draggable>
                                );
                            })}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    )
};
