import React, {FC} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Tabs, Typography} from "@material-ui/core";
import {RootState} from "@src/redux/rootReducer";
import {CustomTabPanel} from '@src/components/elements/custom_tab_panel/CustomTabPanel';
import {CustomTab} from "@src/components/elements/custom_tab/CustomTab";
import {useStyles} from './useStyles';


export const adTypesList = [
    {id: 1, name: 'Объявление'},
    {id: 2, name: 'Торг'},
    {id: 3, name: 'Продвинутый торг'},
];

export const CreateAdModalForm: FC<any> = (props) => {
    const {handleCloseModal} = props;

    const dispatch = useDispatch();
    const categoriesList = useSelector(({categories}: RootState) => categories.list);

    const handleMenuOpen = (setAnchor) => (e) => {
        setAnchor(e.currentTarget);
    };

    const handleMenuClose = (setAnchor) => () => {
        setAnchor(null);
    };

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Tabs>
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Объявления
                        </Typography>
                    }
                    id={0}
                />
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Торги
                        </Typography>
                    }
                    id={1}
                />
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Продвинутые торги
                        </Typography>
                    }
                    id={2}
                />
            </Tabs>
            <CustomTabPanel></CustomTabPanel>
        </div>
    )
};
