import {TOTAL_FILES_LIMIT, TOTAL_FILES_SIZE_LIMIT} from '@src/constants';
import React, {Dispatch, FC} from 'react';
import {DragDropContext, Droppable, Draggable, resetServerContext} from 'react-beautiful-dnd';
import {ButtonComponent} from "@src/components/elements/button/Button";
import {Grid} from "@material-ui/core";
import {CreateAdFields} from "@root/interfaces/Advertisement";
import {useStyles} from './useStyles';
import {CustomDroppable} from "@src/components/advertisement/create_advrt/preview_photos/CustomDroppable";


interface IPreviewPhotos {
    values: CreateAdFields,
    setValues: Dispatch<unknown>
    isPreview: boolean
}

export const PreviewPhotos: FC<IPreviewPhotos> = (props) => {
    resetServerContext();

    const {values, setValues, isPreview} = props;
    const {files} = values;

    const handleOnDragEnd = (result) => {
        const { source, destination } = result;

        // dropped outside the list
        if (!destination) {
            return;
        }

        if (source.droppableId === destination.droppableId) {
            const items = reorder(
                getList(source.droppableId),
                source.index,
                destination.index
            );

            let state = { items };

            if (source.droppableId === 'droppable2') {
                state = { selected: items };
            }

            setState(state);
        } else {
            const result = move(
                getList(source.droppableId),
                getList(destination.droppableId),
                source,
                destination
            );

            setState({
                items: result.firstRow,
                selected: result.secondRow
            });
        }
        if (!result.destination) return;

        const items = Array.from(files);
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);

        setValues({...values, files: items});
    };

    const handleUploadFile = ({target}) => {
        let photos: any = Array.from(target.files);
        if (photos.length + files.length <= TOTAL_FILES_LIMIT) {
            const totalSize = files.reduce((
                total,
                item: {
                    file: { size: number }
                }) => total + item.file.size,
                0
            );
            console.log(photos)
            if ((totalSize + photos[0].size) < TOTAL_FILES_SIZE_LIMIT) {
                photos = photos.map(file => ({
                    file,
                    url: URL.createObjectURL(file)
                }));

                setValues({
                    ...values,
                    files: [
                        ...photos,
                        ...files
                    ]
                })
            }
        }
    };

    const removeFile = (url) => () => {
        files.map((item: { url: string }, index) => {
            if (item.url === url) {
                files.splice(index, 1);
                setValues({...values, files});
            }
        });
    };
    console.log(files)
    const classes = useStyles();
    return (
        <div>
            <input
                type='file'
                disabled={isPreview}
                onChange={handleUploadFile}
                multiple={true}
                accept="image/png,image/jpeg"
            />
            <DragDropContext enableDefaultSensors={true} onDragEnd={handleOnDragEnd}>
                <CustomDroppable
                    droppableId='firstRow'
                    files={files.slice(0, 4)}
                    isPreview={isPreview}
                    removeFile={removeFile}
                />
                <CustomDroppable
                    droppableId='secondRow'
                    files={files.slice(4)}
                    isPreview={isPreview}
                    removeFile={removeFile}
                />
            </DragDropContext>
        </div>
    )
};
