import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
import {userAPI} from '@src/api/api'


const fetchToken = createAsyncThunk(
    'auth/fetchByIdStatus',
    async (phone, password) => {
        const response = await userAPI.login(phone, password)
        return response.data
    }
)

export type AuthTypes = {
    isFetch: boolean,
    isAuth: boolean,
    error?: any
};

const initialState: AuthTypes = {
    isFetch: false,
    isAuth: false,
    error: null
};

const authSlice = createSlice({
    name: 'auth',
    initialState,
    extraReducers: {
        // [setIsAuthAction]: (state, action) => state.isAuth = action.payload,
        [fetchToken.pending]: (state) => ({
            ...state,
            isFetch: true,
            error: null
        }),
        [fetchToken.fulfilled]: (state, action) => ({
            ...state,
            isFetch: false,
            ...action.payload
        }),
        [fetchToken.rejected]: (state, action) => ({
            ...state,
            isFetch: false,
            error: action.payload
        })
    }
});

export const auth = (state) => state.auth;

export const {setIsAuthAction} = authSlice.actions;

export default authSlice.reducer;
