import React, {useState} from "react"
import {AppBar, Button, Grid, Hidden, MenuItem, Select, Toolbar, Typography} from "@material-ui/core";
import {ButtonComponent} from "../../../elements/button/Button";
import {
    Logo,
    CategoryIcon,
    SearchIcon,
    SignIcon,
    AddIcon,
    PlIcon
} from '../../../elements/icons'
import {withScrollThreshold} from "../../../elements/hide_on_scroll/HideOnScroll";
import {useStyles} from "./useStyle"


const BottomHeader = (props) => {
    // const [adType, setAdType] = useState('');
    console.log(props)
    // const handleChange = (e) => {
    //     setAdType(e.target.value);
    // };

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Hidden smDown={true}>
                <AppBar position={"fixed"} color={"inherit"} elevation={0}>
                    <Toolbar>
                        <Grid container justify="space-between" alignItems="center">
                            <Grid container item xs={3} spacing={1} alignItems="center">
                                <Grid container item md={7} lg={6} className="bottom-logo">
                                    <a href="#">
                                        <img src={Logo} alt="Slondo logo"/>
                                    </a>
                                </Grid>
                                <Grid item container md={5} lg={6} justify="center">
                                    <Button
                                        variant="contained"
                                        color="primary"
                                        className="category-button"
                                    >
                                        <Typography variant="subtitle2">Категории</Typography>
                                        <img
                                            src={CategoryIcon}
                                            alt="Category icon"
                                        />
                                    </Button>
                                </Grid>
                            </Grid>
                            <Grid
                                item
                                container
                                md={6}
                                spacing={1}
                                alignItems="center"
                                className="search-block"
                            >
                                <Grid item md={9}>
                                    <div>
                                        <form>
                                            <img
                                                src={SearchIcon}
                                                className="search-icon"
                                            />
                                            <input
                                                type="text"
                                                className="search-input"
                                                placeholder="Поиск по объявлениям"
                                            />
                                            <Button className="search-button">
                                                <Typography variant="subtitle2">
                                                    Найти
                                                </Typography>
                                            </Button>
                                        </form>
                                    </div>
                                </Grid>
                                <Grid className='select-menu' item md={3}>
                                    {/*<Select*/}
                                    {/*    variant={'outlined'}*/}
                                    {/*    value={adType}*/}
                                    {/*    onChange={handleChange}*/}
                                    {/*    displayEmpty*/}
                                    {/*>*/}
                                    {/*    <MenuItem value="">*/}
                                    {/*        <Typography variant='subtitle2'>Тип объявления</Typography>*/}
                                    {/*    </MenuItem>*/}
                                    {/*    <MenuItem value={10}>*/}
                                    {/*        <Typography variant='subtitle2'>test</Typography>*/}
                                    {/*    </MenuItem>*/}
                                    {/*    <MenuItem value={20}>*/}
                                    {/*        <Typography variant='subtitle2'>test2</Typography>*/}
                                    {/*    </MenuItem>*/}
                                    {/*    <MenuItem value={30}>*/}
                                    {/*        <Typography variant='subtitle2'>test3</Typography>*/}
                                    {/*    </MenuItem>*/}
                                    {/*</Select>*/}
                                </Grid>
                            </Grid>
                            <Grid item md={2} className='create-ad'>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    fullWidth
                                    className="add-adv"
                                >
                                    <Typography variant="subtitle2">
                                        Создать объявление
                                    </Typography>
                                    <img
                                        src={AddIcon}
                                        style={{
                                            marginLeft: '10px',
                                            height: '20px',
                                        }}
                                    />
                                </Button>
                            </Grid>
                            <Grid item container alignItems="center" xs={1}>
                                <ButtonComponent
                                    variant="contained"
                                    fullWidth
                                    className="bottom-sign-button"
                                >
                                    <Typography variant="subtitle2">Вход</Typography>
                                    <img
                                        src={SignIcon}
                                        style={{
                                            width: '20px',
                                            marginLeft: '10px',
                                        }}
                                        alt="Sign in"
                                    />
                                </ButtonComponent>
                            </Grid>
                        </Grid>
                    </Toolbar>
                </AppBar>
            </Hidden>
            {/*      Adaptive       */}
            <Hidden mdUp={true}>
                <div className='select-local'>
                    <img src={PlIcon} alt="location"/>
                    {/*<Select*/}
                    {/*    variant={'outlined'}*/}
                    {/*    value={adType}*/}
                    {/*    onChange={handleChange}*/}
                    {/*    displayEmpty*/}
                    {/*>*/}
                    {/*    <MenuItem value="">*/}
                    {/*        <Typography variant='subtitle2'>г. Ташкент, Мирзо-Улугбекский р_н</Typography>*/}
                    {/*    </MenuItem>*/}
                    {/*    <MenuItem value={10}>*/}
                    {/*        <Typography variant='subtitle2'>test</Typography>*/}
                    {/*    </MenuItem>*/}
                    {/*    <MenuItem value={20}>*/}
                    {/*        <Typography variant='subtitle2'>test2</Typography>*/}
                    {/*    </MenuItem>*/}
                    {/*    <MenuItem value={30}>*/}
                    {/*        <Typography variant='subtitle2'>test3</Typography>*/}
                    {/*    </MenuItem>*/}
                    {/*</Select>*/}
                </div>
            </Hidden>
        </div>
    )
};

export default withScrollThreshold(BottomHeader, 200);
