import React, {FC, useState} from 'react';
import {Advertisement} from '@src/components/advertisement/Advertisement';
import {MainLayout} from "@src/components/MainLayout";
import {fetchAdDataForCreate} from "@src/redux/slices/createAdvrtSlice";


const AdvertisementContainer: FC<void> = () => {
    const [isPreview, setIsPreview] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);

    const handlePreview = () => {
        setIsPreview(true)
    };

    const handleSuccess = () => {
        setIsSuccess(true)
    };

    return (
        <div>
            <MainLayout>
                <Advertisement
                    isPreview={isPreview}
                    handlePreview={handlePreview}
                    isSuccess={isSuccess}
                    handleSuccess={handleSuccess}
                />
            </MainLayout>
        </div>
    )
};

export default AdvertisementContainer;
