import { makeStyles } from '@material-ui/core/styles';

export const useStyles = makeStyles((theme) => ({
    root: {
        paddingTop: '24px',
    },
    bottomHeader: {
        [theme.breakpoints.down('sm')]: {
            marginTop: '60px',
        },
        [theme.breakpoints.up('sm')]: {
            marginTop: '10px',
        }
    },
    modalDialog: {
        '& > div:first-child': {
            [theme.breakpoints.down('sm')]: {
                backgroundColor: `${theme.palette.primary.white}!important`,
            },
        },
    },
}));
