import {userApi} from '../../api/api'
import {authErrorAction} from './errorsActions'

export const selfDataActionTypes = {
    GET_SELF_DATA_BEGIN: 'GET_SELF_DATA_BEGIN',
    GET_SELF_DATA_SUCCESS: 'GET_SELF_DATA_SUCCESS',
    GET_SELF_DATA_FAILURE: 'GET_SELF_DATA_FAILURE',
};

export const getSelfData = async (phone, password) => {
    try {
        const {userInfo} = await userApi.login(phone, password);
        return {
            type: selfDataActionTypes.GET_SELF_DATA_BEGIN,
            payload: {
                isFetch: false,
                selfData: userInfo
            }
        }
    } catch (e) {
        authErrorAction(e)
    }
};
