import React, {FC} from 'react'
import {
    Card,
    CardActionArea,
    CardContent,
    CardMedia,
    IconButton,
    Typography,
} from '@material-ui/core'
import Skeleton from '@material-ui/lab/Skeleton';
import {
    FavoriteIcon,
    DeliveryIcon,
    SafeIcon,
    SwapIcon
} from '@src/components/elements/icons';
import {WithT} from "i18next";
import {InnerCardData} from "@root/interfaces/CardData";
import {useStyles} from './useStyles'


export const CardItem: FC<InnerCardData & WithT & { isFetch: boolean }> = (props) => {
    const {
        t,
        isFetch,
        images,
        id,
        delivery,
        safe_deal,
        exchange,
        title,
        price,
        location,
        created_at,
        currency,
        ads_type
    } = props;

    const classes = useStyles(ads_type);
    return (
        <div className={classes.root}>
            <IconButton className='favorite-btn'>
                <FavoriteIcon id={id}/>
            </IconButton>
            <Card
                elevation={0}
                title={title}
                className={classes.root}
            >
                {isFetch
                    ? <Skeleton variant="rect" className={classes.skeleton}/>
                    : <CardMedia
                        className="card-media"
                        image={images.length ? images[0].url.default : null}
                    >
                        <div className="card-header">
                            <Typography variant="subtitle2" className='title'>{t(`common:${ads_type}`)}</Typography>
                            <div className='icons'>
                                {!!delivery && (
                                    <span>
                                    <DeliveryIcon/>
                                </span>
                                )}
                                {!!safe_deal && (
                                    <span>
                                    <SafeIcon/>
                                </span>
                                )}
                                {!!exchange && (
                                    <span>
                                    <SwapIcon/>
                                </span>
                                )}
                            </div>
                        </div>
                    </CardMedia>}
                <CardActionArea>
                    <CardContent>
                        {isFetch
                            ? <>
                                <Skeleton variant="rect"/>
                                <Skeleton variant="rect"/>
                                <Skeleton variant="rect"/>
                                <br/>
                                <Skeleton variant="rect"/>
                            </>
                            : <>
                                <Typography variant="subtitle1" color="initial" noWrap>
                                    {title}
                                </Typography>
                                <Typography variant="h5" color="initial">
                                    {price}
                                    <span>{currency.name}</span>
                                </Typography>
                                <Typography variant="caption" noWrap color="initial" className="card-location">
                                    {location}
                                </Typography>
                                <br/>
                                <Typography variant="caption" noWrap color="initial" className="card-location">
                                    {created_at}
                                </Typography>
                            </>}
                    </CardContent>
                </CardActionArea>
            </Card>
        </div>
    )
}
