import React, {FC} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {useStyles} from './useStyles';


export const AdvrtSettingsBlock: FC<any> = (props) => {
    const {
        isPreview,
        createAdvrt,
        adsParams,
        onBlur,
        handleClickMenuItem
    } = props;

    const data = createAdvrt.data;

    const classes = useStyles();
    return (
        <Grid container
              spacing={1}
              className={classes.root}
        >
            {
                Object.keys(data).map(key => {
                    if (Array.isArray(data[key]) && data[key].length && key !== 'adsParams' && key !== 'address') {
                        return (
                            <Grid
                                container
                                item
                                key={key}
                                spacing={1}
                                xs={12}
                                justify='space-between'
                                sm={key !== 'manufacturer' && key !== 'body' ? 6 : null}
                            >
                                {listGenerator(data[key], key)}
                                {
                                    adsParams[key] && Object.keys(adsParams[key]).map(innerKey => {
                                        if (Array.isArray(adsParams[key][innerKey]) && adsParams[key][innerKey].length) {
                                            return listGenerator(adsParams[key][innerKey], innerKey)
                                        }
                                    })
                                }
                            </Grid>
                        );
                    } else if (isPreview && adsParams[key] && !Array.isArray(adsParams[key])) {
                        return (
                            <Typography variant="subtitle1">
                                <strong>{adsParams[key].name}</strong>
                            </Typography>
                        )
                    }
                })
            }
        </Grid>
    )

    function listGenerator(data, key) {
        let field;

        if (key === 'body') {
            console.log(data)
            field = data.map(body => (
                <div key={body.id}>
                    <img src={body.icon.url} alt={body.name}/>
                    <Typography>{body.name}</Typography>
                </div>
            ));
        } else {
            data = [{id: null, name: 'Не выбрано'}, ...data];
            field = (
                <CustomMenu
                    valueName={adsParams[key] ? adsParams[key].name : data[0].name}
                    items={data}
                    onBlur={onBlur}
                    onClick={handleClickMenuItem(key)}
                />
            );
        }

        return (
            <Grid
                item
                container
                direction='row'
                xs={12}
                sm={key !== 'manufacturer' && key !== 'body' ? 12 : null}
            >
                <Typography variant="subtitle1">
                    <strong>{key}</strong>
                </Typography>
                {field}
            </Grid>
        )
    }
};
