import React, { useState } from 'react'
import { Container, Grid, Typography, Tabs } from '@material-ui/core'
import { CustomTab } from '../elements/custom_tab/CustomTab'
import { CabinetTabItem } from './card/CabinetTabItem'
import { Notification } from '@src/components/cabinet/notification/Notification'
import { CabinetSidebar } from '@src/components/cabinet/cabinetSidebar/CabinetSidebar'


// styles
import { useStyles } from './useStyles'

const TabPanel = (props) => {
    const { children, value, index, ...other } = props

    return (
        <div hidden={value !== index} {...other}>
            {value === index && children}
        </div>
    )
}

export const Cabinet = (props) => {
    const [value, setValue] = useState(0)

    const handleChange = (event, newValue) => {
        setValue(newValue)
    }

    const classes = useStyles()
    return (
        <div className={classes.root}>
            <Container maxWidth="lg">
                <Grid container spacing={2}>
                    <Grid item xs={3}>
                        <CabinetSidebar {...props} />
                    </Grid>
                    <Grid item xs={9}>
                        <Typography variant='h6' className='menu-title'>
                            Мои объявления
                        </Typography>
                        <Grid item xs={9}>
                            <Tabs
                                value={value}
                                onChange={handleChange}
                                indicatorColor="primary"
                            >
                                <CustomTab
                                    label={
                                        <Typography variant="subtitle1">
                                            Объявления (2)
                                        </Typography>
                                    }
                                    id={0}
                                />
                                <CustomTab
                                    label={
                                        <Typography variant="subtitle1">
                                            Торги (2)
                                        </Typography>
                                    }
                                    id={1}
                                />
                                <CustomTab
                                    label={
                                        <Typography variant="subtitle1">
                                            продвинутые торги
                                        </Typography>
                                    }
                                    id={2}
                                />
                            </Tabs>
                        </Grid>
                        <TabPanel value={value} index={0}>
                            <CabinetTabItem />
                        </TabPanel>
                        <TabPanel value={value} index={1}>
                            <Notification />
                        </TabPanel>
                        <TabPanel value={value} index={2}>
                            <CabinetTabItem />
                            <CabinetTabItem />
                            <CabinetTabItem />
                        </TabPanel>
                    </Grid>
                </Grid>
            </Container>
        </div>
    )
}
