import React, {Dispatch, FC, SetStateAction} from 'react';
import {Grid, Typography, ButtonBase} from '@material-ui/core';
import {useStyles} from './useStyles';
import {AncmntType} from "@root/interfaces/Announcement";


type AncmntTypesPageProps = {
    ancmntTypes: AncmntType[];
    setAncmntType: Dispatch<SetStateAction<AncmntType[]>>
};

const urls = [
    '/img/adv-background.png',
    '/img/lot-background.png',
    '/img/advanced-lot-background.png',
];

export const AncmntTypesPage: FC<AncmntTypesPageProps> = ({ancmntTypes, setAncmntType}) => {
    const imageUrls = {urls};

    const handleSetAncmntType = () => (ancmntType) => {
        setAncmntType(ancmntType);
    };

    const classes = useStyles(imageUrls);
    return (
        <div className={classes.root}>
            <Grid container spacing={2}>
                {ancmntTypes.map(anType => {
                    return (
                        <Grid item xs={4}>
                            <ButtonBase className='regular'>
                                <div>
                                    <Typography variant="subtitle1">Объявление</Typography>
                                    <Typography variant="body2">
                                        Размещайте товары или услуги совершенно бесплатно
                                    </Typography>
                                </div>
                                {/*<ButtonComponent>*/}
                                {/*    Создать объявление*/}
                                {/*</ButtonComponent>*/}
                            </ButtonBase>
                        </Grid>
                    )
                })}
                <Grid item xs={4}>
                    <ButtonBase className='regular'>
                        <div>
                            <Typography variant="subtitle1">Объявление</Typography>
                            <Typography variant="body2">
                                Размещайте товары или услуги совершенно бесплатно
                            </Typography>
                        </div>
                        {/*<ButtonComponent>*/}
                        {/*    Создать объявление*/}
                        {/*</ButtonComponent>*/}
                    </ButtonBase>
                </Grid>
                <Grid item xs={4}>
                    <ButtonBase className='auction'>
                        <div>
                            <Typography variant="subtitle1">Онлайн аукцион</Typography>
                            <Typography variant="body2">
                                Торгуйте что бы получить более выгодную цену для Вас
                            </Typography>
                        </div>
                        {/*<ButtonComponent>*/}
                        {/*    Создать аукцион*/}
                        {/*</ButtonComponent>*/}
                    </ButtonBase>
                </Grid>
                <Grid item xs={4}>
                    <ButtonBase className='adv-auction'>
                        <div>
                            <Typography variant="subtitle1">Продвинутый аукцион</Typography>
                            <Typography variant="body2">Используйте максимальный функционал</Typography>
                        </div>
                        {/*<ButtonComponent>*/}
                        {/*    Создать продвинутый аукцион*/}
                        {/*</ButtonComponent>*/}
                    </ButtonBase>
                </Grid>
            </Grid>
        </div>
    );
};
