import {makeStyles} from '@material-ui/core/styles';


export const useStyles = makeStyles(() => ({
    root: {
        display: 'flex',
        justifyContent: 'center',
        width: '50px',
        height: '50px',
        backgroundColor: '#fafafa',
        boxShadow: '0px 0px 12px rgba(0, 0, 0, 0.15)',
        borderRadius: '100px',
        zIndex: 20,
        '& span.MuiIconButton-label': {
            position: 'relative',
            width: '12px',
            height: '12px',
            transform: 'rotate(-45deg)',
            '&:before': {
                content: '""',
                position: 'absolute',
                bottom: '0px',
                right: '0px',
                background: 'linear-gradient(49.94deg, #AD66D5 73%, #675EAA 20%)',
                width: 'inherit',
                height: '3px',
            },
            '&:after': {
                content: '""',
                position: 'absolute',
                top: '0px',
                right: '0px',
                background: 'linear-gradient(49.94deg, #675EAA 19.03%, #AD66D5 72.72%)',
                height: 'inherit',
                width: '3px',
            }
            // '& i.prev': {
            //     transform: 'rotate(135deg)',
            // },
            // '& i.next': {
            //     transform: 'rotate(-45deg)',
            // }
        },
        '&:hover': {
            backgroundColor: '#fafafa'
        }
    }
}));
