import {Typography} from '@material-ui/core';
import React, {Dispatch, FC} from 'react';
import {DragDropContext, Droppable, Draggable, resetServerContext} from 'react-beautiful-dnd';


export const DropZone: FC<{ images: [], setImages: Dispatch<any> }> = (props) => {
    const {images = [{id: 1, name: 'test'}, {id: 2, name: 'test2'}], setImages} = props;

    function handleOnDragEnd(result) {
        if (!result.destination) return;

        const items = Array.from(images);
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);

        setImages(items);
    }

    resetServerContext();

    return (
        <div className="App">
            <header className="App-header">
                <input type='file'/>
                <DragDropContext onDragEnd={handleOnDragEnd}>
                    <Droppable droppableId="characters" isDropDisabled={false}>
                        {(provided) => (
                            <ul className="characters" {...provided.droppableProps} ref={provided.innerRef}>
                                {images.map(({id, name}, index) => {
                                    return (
                                        <Draggable key={id} draggableId={id} index={index}>
                                            {(provided) => (
                                                <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                                    <div className="characters-thumb">
                                                        {/*<img src={url} alt={`${name} Thumb`}/>*/}
                                                        <Typography>{name}</Typography>
                                                    </div>
                                                </li>
                                            )}
                                        </Draggable>
                                    );
                                })}
                                {provided.placeholder}
                            </ul>
                        )}
                    </Droppable>
                </DragDropContext>
            </header>
        </div>
    )
};
