import React, {useRef} from 'react'
import SlickSlider from 'react-slick'
import {Container, Typography} from '@material-ui/core'
import IconButton from '@material-ui/core/IconButton'
import ArrowLeftIcon from '@material-ui/icons/ArrowLeft'
import ArrowRightIcon from '@material-ui/icons/ArrowRight'
import {CarIcon} from '../../../elements/icons'
import {useStyles} from './useStyle'


export const CategorySlider = () => {
    const slider = useRef();
    const next = () => {
        slider.current.slickNext()
    }
    const previous = () => {
        slider.current.slickPrev()
    }

    const classes = useStyles();
    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        adaptiveHeight: true,
        autoplay: false,
        centerMode: false,
        arrows: false,
        beforeChange: this.beforeChange,
    }

    return (
        <div className={classes.root}>
            <Container className="category-slider">
                <Typography variant="h4">Популярные категории</Typography>
                <SlickSlider ref={slider} {...settings}>
                    {this.state.slides.map(({index}) => (
                        <div
                            className="category-item"
                            key={index}
                            style={{
                                width: 'auto',
                                display: 'flex !important',
                                flexFlow: 'column wrap',
                                alignItems: 'center',
                                fontSize: '1rem',
                                marginRight: '20px',
                            }}
                        >
                            <a href="#" key={index}>
                                <img src={CarIcon}/>
                                <p>Легковые автомобили</p>
                            </a>
                        </div>
                    ))}
                </SlickSlider>

                <IconButton
                    className="left-button"
                    size="small"
                    disabled={index === 0}
                    onClick={previous}
                >
                    <ArrowLeftIcon fontSize="large"/>
                </IconButton>
                <IconButton
                    className="right-button"
                    size="small"
                    disabled={index === slides.length - 1}
                    onClick={next}
                >
                    <ArrowRightIcon fontSize="large"/>
                </IconButton>
            </Container>
        </div>
    )
};
