import React, {FC, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Tabs, Typography, InputBase} from "@material-ui/core";
import {RootState} from "@src/redux/rootReducer";
import {CustomTabPanel} from '@src/components/elements/custom_tab_panel/CustomTabPanel';
import {CustomTab} from "@src/components/elements/custom_tab/CustomTab";
import {useStyles} from './useStyles';


export const adTypesList = [
    {id: 1, name: 'Объявление'},
    {id: 2, name: 'Торг'},
    {id: 3, name: 'Продвинутый торг'},
];

export const CreateAdModalForm: FC<any> = (props) => {
    const {handleCloseModal} = props;

    const dispatch = useDispatch();
    const categoriesList = useSelector(({categories}: RootState) => categories.list);

    const [tabValue, setTabValue] = useState(0);

    const handleTab = (_, newValue) => {
        setTabValue(newValue);
    };

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Tabs
                value={tabValue}
                onChange={handleTab}
            >
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Объявления
                        </Typography>
                    }
                    id={0}
                />
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Торги
                        </Typography>
                    }
                    id={1}
                />
                <CustomTab
                    label={
                        <Typography variant="subtitle1">
                            Продвинутые торги
                        </Typography>
                    }
                    id={2}
                />
            </Tabs>
            <div>
                <div className='top-search'>
                    <Typography>Выберите категорию</Typography>
                    <InputBase/>
                </div>
                <CustomTabPanel value={tabValue} index={0}>
                    <div>Test</div>
                </CustomTabPanel>
                <CustomTabPanel value={tabValue} index={1}>
                    <div>Test2</div>
                </CustomTabPanel>
                <CustomTabPanel value={tabValue} index={2}>
                    <div>Test3</div>
                </CustomTabPanel>
            </div>
        </div>
    )
};
